import {
	isProgramError,
	type Address,
	type SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM,
	type SolanaError,
} from '@solana/kit';
import { PYTHIA_OP_PROGRAM_ADDRESS } from '../programs';

/** InvalidCallbackTx: Invalid callback transaction */
export const PYTHIA_OP_ERROR__INVALID_CALLBACK_TX = 0x1770; // 6000

export type PythiaOpError = typeof PYTHIA_OP_ERROR__INVALID_CALLBACK_TX;

let pythiaOpErrorMessages: Record<PythiaOpError, string> | undefined;
if (process.env.NODE_ENV !== 'production') {
	pythiaOpErrorMessages = {
		[PYTHIA_OP_ERROR__INVALID_CALLBACK_TX]: `Invalid callback transaction`,
	};
}

export function getPythiaOpErrorMessage(code: PythiaOpError): string {
	if (process.env.NODE_ENV !== 'production') {
		return (pythiaOpErrorMessages as Record<PythiaOpError, string>)[code];
	}

	return 'Error message not available in production bundles.';
}

export function isPythiaOpError<TProgramErrorCode extends PythiaOpError>(
	error: unknown,
	transactionMessage: {
		instructions: Record<number, { programAddress: Address }>;
	},
	code?: TProgramErrorCode,
): error is SolanaError<typeof SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM> &
	Readonly<{ context: Readonly<{ code: TProgramErrorCode }> }> {
	return isProgramError<TProgramErrorCode>(
		error,
		transactionMessage,
		PYTHIA_OP_PROGRAM_ADDRESS,
		code,
	);
}
